<script>
	import { fillColor, lettersColor, lettersOutline } from "./store.js";
	import { afterUpdate } from "svelte";

	function mapAndApplyColor(node, property, color) {
	  node.forEach(el => {
	    if (property === "fill") {
	      el.style.fill = color;
	    } else {
	      el.style.stroke = color;
	    }
	  });
	}

	afterUpdate(() => {
	  const CLS1 = document.querySelectorAll(".cls-1");
	  const CLS2 = document.querySelectorAll(".cls-2");
	  const CLS3 = document.querySelectorAll(".cls-3");

	  mapAndApplyColor(CLS1, "fill", $fillColor);
	  mapAndApplyColor(CLS2, "fill", $lettersColor);
	  mapAndApplyColor(CLS3, "stroke", $lettersOutline);
	});
</script>



